/* only return new items */
const staticData = getWorkflowStaticData('global');
const lastItemId = staticData.lastItemId;

console.log('lastItemId', lastItemId);
const firstItem = items[0];
let newItems = [];

function getId(item) {
  return item.json.guid;
}

if (lastItemId) {
  for (const item of items) {
    if (getId(item) === lastItemId) {
      break;
    }
    newItems.push(item)
  }
} else {
  newItems = [firstItem]
}

staticData.lastItemId = getId(firstItem)
return newItems

/* filter items */
const newItems = [];

const SYMBOL_MOVIE = '📺'
const SYMBOL_BOOK = '📖'

const tagInfoMap = {
  watched: { re: /看过/, symbol: SYMBOL_MOVIE },
  watching: { re: /在看/, symbol: SYMBOL_MOVIE },
  read: { re: /读过/, symbol: SYMBOL_BOOK },
  reading: { re: /在读/, symbol: SYMBOL_BOOK },
}

for (const item of items) {
  const title = item.json.title
  let tag, symbol

  for (const key in tagInfoMap) {
    const tagInfo = tagInfoMap[key]
    if (tagInfo.re.test(title)) {
      tag = key
      symbol = tagInfo.symbol
      break
    }
  }

  if (tag) {
    item.json.tag = tag
    item.json.tgTitle = `${symbol} ${title} #douban #${tag}`
    newItems.push(item)
  }
}

return newItems
